package Condicionais;

public class Exe01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a; 
		
		a = 10;
		//resto = 10 % 2;
		
		if(a % 2 == 0) {
			System.out.println("O número " + a + " é par");	
		} // fim se
		else {
			System.out.println("O número " + a + " é ímpar");
		} // fim else
		
	
		
	} // fim main

} // fim class